const path = require("path");

function isPathRelative(path) {
  return path === '.' || path.startsWith('./') || path.startsWith('../')
}

const getNormalizedPath = (from) => {
  const fromNormalizedPath = path.toNamespacedPath(from);
  const isWindowsOS = fromNormalizedPath.includes("\\");
  const fromPath = fromNormalizedPath.split("src")[1];

  return fromPath.split(isWindowsOS ? "\\" : "/");
};

const createLinkToRule = (name) => {
  return `https://github.com/eslint-plugins/blob/master/rules/${name}/${name}.md`
}

module.exports = { getNormalizedPath, createLinkToRule, isPathRelative };
